export default function AdminDashboard({ orders, onSelectUser }) {
  const users = [...new Set(orders.map(o => o.user))];

  return (
    <div className="bg-white p-6 rounded shadow">
      <h2 className="text-xl font-bold mb-4">Admin Dashboard</h2>

      <div className="flex gap-2 mb-4">
        {users.map(u => (
          <button
            key={u}
            onClick={() => onSelectUser(u)}
            className="bg-orange-200 px-3 py-1 rounded"
          >
            {u}
          </button>
        ))}
      </div>

      {orders.map(o => (
        <div key={o.id} className="text-sm border-b py-1">
          {o.user} • {o.item} • {o.status}
        </div>
      ))}
    </div>
  );
}
