import { useState, useEffect, useRef } from "react";
import {
  login,
  getMenu,
  addOrder,
  cancelOrder,
  checkout,
  adminOrders,
  myOrders
} from "./api";

import Login from "./components/login";
import Navbar from "./components/Navbar";
import RestaurantList from "./components/RestaurantList";
import Cart from "./components/Cart";
import AdminDashboard from "./components/AdminDashboard";
import OrderHistory from "./components/OrderHistory";
import Toast from "./components/Toast";

export default function App() {
  const [token, setToken] = useState("");
  const [role, setRole] = useState("");
  const [menu, setMenu] = useState({});
  const [orders, setOrders] = useState([]);
  const [payment, setPayment] = useState("CREDIT_CARD");
  const [toasts, setToasts] = useState([]);

  const historyRef = useRef(null);

  const showToast = (message) => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message }]);
    setTimeout(() => setToasts((prev) => prev.filter(t => t.id !== id)), 3000);
  };

  const handleLogin = async (u) => {
    const r = await login(u);
    setToken(r.token);
    setRole(r.role);
    setMenu(await getMenu(r.token));
    if (r.role !== "ADMIN") {
      setOrders(await myOrders(r.token));
    }
    showToast("Logged in successfully!");
  };

  const handleAddOrder = async (item, price) => {
    await addOrder(token, item, price);
    setOrders(await myOrders(token));
    showToast(`${item} added to cart!`);
  };

  const handleCancelOrder = async (id) => {
    await cancelOrder(id, token);
    setOrders(await myOrders(token));
    showToast("Order cancelled!");
  };

  const handleCheckout = async () => {
    await checkout(token, payment);
    setOrders(await myOrders(token));
    showToast("Checkout successful!");
  };

  // ADMIN LIVE UPDATES
  useEffect(() => {
    if (role === "ADMIN") {
      const i = setInterval(() => {
        adminOrders(token).then(setOrders);
      }, 1000);
      return () => clearInterval(i);
    }
  }, [role, token]);

  // AUTO SHOW ORDER HISTORY
  useEffect(() => {
    const hasPaidOrder = orders.some(o => o.status === "PAID");
    if (hasPaidOrder && historyRef.current) {
      historyRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [orders]);

  if (!token) return <Login onLogin={handleLogin} />;

  return (
    <div className="bg-[#FDF5E6] min-h-screen font-sans">
      <Navbar role={role} logout={() => window.location.reload()} />
      <div className="p-6">
        {role === "ADMIN" && <AdminDashboard orders={orders} menu={menu} onAddOrder={handleAddOrder} onCancel={handleCancelOrder} />}
        
        {role !== "ADMIN" && (
          <div className="grid grid-cols-3 gap-6">
            <div className="col-span-2">
              <RestaurantList menu={menu} onAdd={handleAddOrder} />
            </div>

            <div>
              <Cart
                orders={orders.filter(o => o.status === "CREATED")}
                onCancel={handleCancelOrder}
              />
              {role !== "MEMBER" && (
                <div className="bg-white p-4 rounded-xl shadow mt-4">
                  <select
                    className="border p-2 w-full rounded mb-2"
                    onChange={(e) => setPayment(e.target.value)}
                  >
                    <option>CREDIT_CARD</option>
                    <option>UPI</option>
                    <option>CASH</option>
                  </select>

                  <button
                    onClick={handleCheckout}
                    className="w-full bg-[#E2725B] hover:bg-[#C05A48] text-white py-2 rounded font-semibold transition transform hover:scale-105"
                  >
                    Checkout
                  </button>
                </div>
              )}

              <div ref={historyRef}>
                <OrderHistory
                  orders={orders.filter(o => o.status === "PAID")}
                />
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Toast Notifications */}
      <div className="fixed bottom-5 right-5 flex flex-col gap-2 z-50">
        {toasts.map(t => <Toast key={t.id} message={t.message} />)}
      </div>
    </div>
  );
}
