const img = "https://images.unsplash.com/photo-1633945274309-2c16f5c9e3c5";

export default function RestaurantList({ menu, onAdd }) {
  return Object.entries(menu).map(([c, items])=>(
    <div key={c} className="bg-white p-4 rounded shadow mb-4">
      <h3 className="font-bold mb-2">üìç {c}</h3>
      {items.map(i=>(
        <div key={i.item} className="flex items-center gap-4 mb-2">
          <img src={img} className="w-16 h-16 rounded"/>
          <div className="flex-1">
            <p>{i.item}</p>
            <p className="text-sm">‚Çπ{i.price}</p>
          </div>
          <button onClick={()=>onAdd(i.item,i.price)}
            className="bg-orange-500 text-white px-3 py-1 rounded">
            Add
          </button>
        </div>
      ))}
    </div>
  ));
}
