import { useEffect } from "react";

export default function useSocket(setOrders) {
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:8000/ws");

    ws.onmessage = (e) => {
      setOrders(JSON.parse(e.data));
    };

    return () => ws.close();
  }, []);
}
