const BASE = "http://localhost:8000";

export const getRestaurants = (user) =>
  fetch(`${BASE}/restaurants?username=${user}`).then(res => res.json());

export const createOrder = (user) =>
  fetch(`${BASE}/order?username=${user}&restaurant_id=1&item=Biryani`, {
    method: "POST"
  });
