import { useState, useEffect } from "react";
import {
  login,
  getMenu,
  addOrder,
  cancelOrder,
  checkout,
  adminOrders
} from "./api";

import Login from "./components/login";
import Navbar from "./components/Navbar";
import RestaurantList from "./components/RestaurantList";
import Cart from "./components/Cart";
import AdminDashboard from "./components/AdminDashboard";

export default function App() {
  const [token, setToken] = useState("");
  const [role, setRole] = useState("");
  const [menu, setMenu] = useState({});
  const [orders, setOrders] = useState([]);
  const [payment, setPayment] = useState("CREDIT_CARD");

  const handleLogin = async (u) => {
    const r = await login(u);
    setToken(r.token);
    setRole(r.role);
    setMenu(await getMenu(r.token));
  };

  // REAL-TIME ADMIN UPDATES
  useEffect(() => {
    if (role === "ADMIN") {
      const i = setInterval(() => {
        adminOrders(token).then(setOrders);
      }, 1000);
      return () => clearInterval(i);
    }
  }, [role, token]);

  if (!token) return <Login onLogin={handleLogin} />;

  return (
    <>
      <Navbar role={role} logout={() => window.location.reload()} />

      {/* ADMIN LAYOUT */}
      {role === "ADMIN" ? (
        <div className="p-6 grid grid-cols-3 gap-6">
          {/* LEFT SIDE */}
          <div className="col-span-2 space-y-6">
            <RestaurantList
              menu={menu}
              onAdd={(i, p) => addOrder(token, i, p)}
            />

            <Cart
              orders={orders.filter(o => o.status === "CREATED")}
              onCancel={(id) => cancelOrder(id, token)}
            />

            <div className="bg-white p-4 rounded-xl shadow">
              <select
                className="border p-2 w-full rounded mb-2"
                onChange={(e) => setPayment(e.target.value)}
              >
                <option>CREDIT_CARD</option>
                <option>UPI</option>
                <option>CASH</option>
              </select>

              <button
                onClick={() => checkout(token, payment)}
                className="w-full bg-green-500 hover:bg-green-600 text-white py-2 rounded"
              >
                Checkout
              </button>
            </div>
          </div>

          {/* RIGHT SIDE â€“ ADMIN DASHBOARD (TOP) */}
          <div className="col-span-1">
            <AdminDashboard orders={orders} />
          </div>
        </div>
      ) : (
        /* NON-ADMIN LAYOUT */
        <div className="p-6 grid grid-cols-3 gap-6">
          <div className="col-span-2">
            <RestaurantList
              menu={menu}
              onAdd={(i, p) => addOrder(token, i, p)}
            />
          </div>

          <div>
            <Cart
              orders={orders.filter(o => o.status === "CREATED")}
              onCancel={(id) => cancelOrder(id, token)}
            />
          </div>
        </div>
      )}
    </>
  );
}
