import { useState, useEffect } from "react";
import Login from "./components/login";
import RestaurantList from "./components/RestaurantList";
import AdminDashboard from "./components/AdminDashboard";
import { login, getMenu, addOrder, adminOrders } from "./api";

export default function App() {
  const [token,setToken]=useState("");
  const [role,setRole]=useState("");
  const [menu,setMenu]=useState({});
  const [orders,setOrders]=useState([]);

  const onLogin=async(r)=>{
    const res=await login(r);
    setToken(res.token);
    setRole(res.role);
    setMenu(await getMenu(res.token));
  };

  useEffect(()=>{
    if(role==="ADMIN"){
      const i=setInterval(()=>adminOrders(token).then(setOrders),1000);
      return()=>clearInterval(i);
    }
  },[role,token]);

  if(!token) return <Login onLogin={onLogin}/>;

  return (
    <div className="p-6 max-w-5xl mx-auto">
      {role==="ADMIN" && <AdminDashboard orders={orders}/>}
      <RestaurantList menu={menu} onAdd={(i,p)=>addOrder(token,i,p)}/>
    </div>
  );
}
