import { useState } from "react";
import CategoryBadge from "./CategoryBadge";

const images = {
  Biryani: "https://images.unsplash.com/photo-1626777552726-4a6b54c97e46",
  Burger: "https://images.unsplash.com/photo-1550547660-d9450f859349"
};

const category = {
  Biryani: "NON_VEG",
  Burger: "VEG"
};

export default function RestaurantList({ menu, onAdd }) {
  const [anim, setAnim] = useState(null);

  return Object.entries(menu).map(([country, items]) => (
    <div key={country} className="card p-5 mb-6">
      <h3 className="font-bold mb-4 text-[color:var(--secondary)]">
        üìç {country}
      </h3>

      {items.map(i => (
        <div key={i.item} className="flex gap-4 items-center mb-4">
          <img src={images[i.item]} className="w-16 h-16 rounded-lg shadow-sm" />
          <div className="flex-1">
            <p className="font-semibold">{i.item}</p>
            <CategoryBadge type={category[i.item]} />
            <p className="font-bold text-[color:var(--primary)]">‚Çπ{i.price}</p>
          </div>

          <button
            onClick={() => {
              setAnim(i.item);
              onAdd(i.item, i.price);
              setTimeout(() => setAnim(null), 200);
            }}
            className={`btn-primary ${anim === i.item ? "pop" : ""}`}
          >
            Add
          </button>
        </div>
      ))}
    </div>
  ));
}
