const BASE = "http://localhost:8000";

// ---------- AUTH ----------
export const login = (u) =>
  fetch(`${BASE}/login?username=${u}`, {
    method: "POST",
  }).then(r => r.json());

// ---------- MENU ----------
export const getMenu = (token) =>
  fetch(`${BASE}/menu`, {
    headers: { token },
  }).then(r => r.json());

// ---------- ORDERS ----------
export const addOrder = (token, item, price) =>
  fetch(`${BASE}/order?item=${item}&price=${price}`, {
    method: "POST",
    headers: { token },
  });

export const myOrders = (token) =>
  fetch(`${BASE}/orders/me`, {
    headers: { token },
  }).then(r => r.json());

export const cancelOrder = (id, token) =>
  fetch(`${BASE}/order/${id}`, {
    method: "DELETE",
    headers: { token },
  });

export const checkout = (token, payment) =>
  fetch(`${BASE}/checkout?payment=${payment}`, {
    method: "POST",
    headers: { token },
  });

// ---------- ADMIN ----------
export const adminOrders = (token) =>
  fetch(`${BASE}/admin/orders`, {
    headers: { token },
  }).then(r => r.json());

export const addPaymentMethod = (token, method) =>
  fetch(`${BASE}/admin/payment-methods?method=${method}`, {
    method: "POST",
    headers: { token },
  }).then(r => r.json());
