import { useEffect, useState } from "react";
import Login from "./Login";
import AdminDashboard from "./AdminDashboard";
import { getRestaurants, createOrder, checkoutOrder, updatePayment } from "./api";

function App() {
  const [user, setUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [toast, setToast] = useState("");

  useEffect(() => {
    if (user) getRestaurants(user).then(setRestaurants);
  }, [user]);

  const notify = msg => {
    setToast(msg);
    setTimeout(() => setToast(""), 2000);
  };

  if (!user) return <Login setUser={setUser} />;

  return (
    <div>
      {toast && (
        <div className="fixed top-6 right-6 bg-green-500 text-white px-4 py-2 rounded">
          {toast}
        </div>
      )}

      <nav className="bg-white shadow px-6 py-4 flex justify-between">
        <h1 className="font-bold text-orange-600">FoodDash</h1>
        <button onClick={() => setUser(null)}>Logout</button>
      </nav>

      <main className="p-8 grid md:grid-cols-3 gap-6">
        <div className="md:col-span-2">
          {restaurants.map(r => (
            <div key={r.id} className="bg-white p-6 mb-4 rounded-xl shadow">
              <h3 className="font-bold">{r.name}</h3>
              <button
                onClick={() => {
                  createOrder(user, r.id, "Biryani");
                  notify("Order added");
                }}
                className="mt-2 bg-orange-500 text-white px-4 py-2 rounded"
              >
                Add Biryani
              </button>
            </div>
          ))}
        </div>

        <div className="bg-white p-6 rounded-xl shadow space-y-4">
          {user === "nick" && (
            <>
              <h3 className="font-bold">Payment Method</h3>
              <select
                value={paymentMethod}
                onChange={(e) => setPaymentMethod(e.target.value)}
                className="w-full border rounded p-2"
              >
                <option value="">Select method</option>
                <option value="UPI">UPI</option>
                <option value="CREDIT_CARD">Credit Card</option>
                <option value="COD">Cash on Delivery</option>
              </select>

              <button
                onClick={async () => {
                  if (!paymentMethod) return notify("Select payment method");
                  await updatePayment(user, paymentMethod);
                  notify(`Payment set to ${paymentMethod}`);
                }}
                className="w-full bg-blue-600 text-white py-2 rounded"
              >
                Update Payment
              </button>
            </>
          )}

          <button
            onClick={async () => {
              await checkoutOrder(user);
              notify("Payment successful");
            }}
            className="w-full bg-green-600 text-white py-3 rounded font-bold"
          >
            Checkout
          </button>

          {user === "nick" && <AdminDashboard user={user} />}
        </div>
      </main>
    </div>
  );
}

export default App;
