import { useState, useEffect } from "react";
import Login from "./Login";
import {
  getRestaurants,
  createOrder,
  checkoutOrder,
  cancelOrder,
  updatePayment
} from "./api";

function App() {
  const [user, setUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [orders, setOrders] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (user) fetchData();
  }, [user]);

  async function fetchData() {
    setLoading(true);
    const data = await getRestaurants(user);
    setRestaurants(data);
    setLoading(false);
  }

  const handleOrder = async (restaurant_id, item) => {
    const result = await createOrder(user, restaurant_id, item);
    if (!result.error) {
      setOrders([...orders, { item, id: Date.now() }]);
    } else {
      alert(result.message);
    }
  };

  if (!user) return <Login setUser={setUser} />;

  return (
    <div className="min-h-screen">
      {/* Navbar */}
      <nav className="bg-white shadow px-8 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-orange-600">FoodDash</h1>
        <div className="flex items-center gap-4">
          <span className="text-sm bg-orange-100 text-orange-600 px-3 py-1 rounded-full">
            {user.toUpperCase()}
          </span>
          <button
            onClick={() => setUser(null)}
            className="text-red-500 font-medium hover:underline"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Main */}
      <main className="max-w-6xl mx-auto p-8 grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Restaurants */}
        <div className="md:col-span-2">
          <h2 className="text-xl font-semibold mb-4">Available Restaurants</h2>

          {loading ? (
            <p>Loading...</p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {restaurants.map((r) => (
                <div
                  key={r.id}
                  className="bg-white p-6 rounded-xl shadow border"
                >
                  <h3 className="font-bold text-lg">{r.name}</h3>
                  <p className="text-sm text-gray-500 mb-4">
                    üìç {r.country}
                  </p>

                  <div className="flex gap-2">
                    <button
                      onClick={() => handleOrder(r.id, "Biryani")}
                      className="flex-1 bg-orange-500 text-white py-2 rounded-lg"
                    >
                      Biryani
                    </button>
                    <button
                      onClick={() => handleOrder(r.id, "Pizza")}
                      className="flex-1 border border-orange-500 text-orange-500 py-2 rounded-lg"
                    >
                      Pizza
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Cart */}
        <div className="space-y-6">
          <div className="bg-white p-6 rounded-xl shadow border">
            <h2 className="font-bold mb-4">Your Orders</h2>

            {orders.length === 0 ? (
              <p className="text-gray-400 text-center">Cart is empty</p>
            ) : (
              <ul className="space-y-2 mb-4">
                {orders.map((o) => (
                  <li
                    key={o.id}
                    className="flex justify-between border-b pb-1 text-sm"
                  >
                    <span>{o.item}</span>
                    <span className="text-green-600 font-semibold">CREATED</span>
                  </li>
                ))}
              </ul>
            )}

            <button
              onClick={async () => {
                await checkoutOrder(user);
                setOrders([]);
              }}
              disabled={!orders.length}
              className="w-full bg-green-600 text-white py-2 rounded-lg disabled:bg-gray-300"
            >
              Checkout
            </button>

            <button
              onClick={async () => {
                await cancelOrder(user);
                setOrders([]);
              }}
              disabled={!orders.length}
              className="w-full text-red-500 text-sm mt-2"
            >
              Cancel All
            </button>
          </div>

          {/* Payment */}
          <div className="bg-white p-6 rounded-xl shadow border">
            <h2 className="font-bold mb-3">Payment</h2>

            <select
              className="w-full border p-2 rounded mb-3"
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
            >
              <option value="">Select Method</option>
              <option value="UPI">UPI</option>
              <option value="Credit Card">Credit Card</option>
              <option value="Cash">Cash</option>
            </select>

            <button
              onClick={() => updatePayment(user, paymentMethod)}
              className="w-full border py-2 rounded hover:bg-gray-50"
            >
              Update Payment
            </button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
