import { useState, useEffect } from "react";

const images = [
  "https://images.unsplash.com/photo-1600891964599-f61ba0e24092",
  "https://images.unsplash.com/photo-1606756790138-261d2b21cd75",
  "https://images.unsplash.com/photo-1540189549336-e6e99c3679fe",
];

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [index, setIndex] = useState(0);

  // üîÅ Background slider animation
  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative h-screen w-full overflow-hidden">
      {/* BACKGROUND SLIDER */}
      <div
        className="absolute inset-0 bg-cover bg-center transition-all duration-1000 scale-105"
        style={{ backgroundImage: `url(${images[index]})` }}
      />

      {/* ORANGE OVERLAY */}
      <div className="absolute inset-0 bg-orange-600/70" />

      {/* LOGIN CARD */}
      <div className="relative z-10 flex items-center justify-center h-full">
        <div className="bg-white w-96 p-8 rounded-2xl shadow-2xl fade-in">
          <h1 className="text-3xl font-bold text-orange-600 text-center mb-2">
            FoodDash üçî
          </h1>
          <p className="text-center text-gray-500 mb-6">
            Login to order delicious food
          </p>

          <input
            className="border w-full p-3 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-orange-400"
            placeholder="Enter username (nick, marvel, thanos...)"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          <button
            onClick={() => onLogin(username)}
            className="w-full bg-orange-500 hover:bg-orange-600 text-white py-3 rounded-lg font-semibold transition"
          >
            Login
          </button>

          <div className="text-xs text-gray-400 mt-4 text-center">
            Demo Users: nick | marvel | america | thanos | thor | travis
          </div>
        </div>
      </div>
    </div>
  );
}
