import { useState, useEffect } from "react";
import Login from "./login";
import AdminDashboard from "./AdminDashboard";
import {
  getRestaurants,
  createOrder,
  checkoutOrder,
  cancelOrder,
  updatePayment
} from "./api";

const PRICE = { Biryani: 250, Pizza: 200 };

function App() {
  const [user, setUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [orders, setOrders] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [toast, setToast] = useState("");

  useEffect(() => {
    if (user) getRestaurants(user).then(setRestaurants);
  }, [user]);

  const addOrder = async (rid, item) => {
    await createOrder(user, rid, item);
    const existing = orders.find(o => o.item === item);
    if (existing) {
      existing.qty += 1;
      existing.total += PRICE[item];
      setOrders([...orders]);
    } else {
      setOrders([...orders, {
        id: Date.now(),
        item,
        qty: 1,
        total: PRICE[item]
      }]);
    }
    showToast("Item added to cart");
  };

  const showToast = msg => {
    setToast(msg);
    setTimeout(() => setToast(""), 2000);
  };

  if (!user) return <Login setUser={setUser} />;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navbar */}
      <nav className="bg-white px-8 py-4 shadow flex justify-between">
        <h1 className="text-2xl font-bold text-orange-600">FoodDash</h1>
        <div className="flex gap-4">
          <span className="bg-orange-100 px-3 py-1 rounded-full">{user}</span>
          <button onClick={() => setUser(null)} className="text-red-500">Logout</button>
        </div>
      </nav>

      {/* Toast */}
      {toast && (
        <div className="fixed top-5 right-5 bg-green-500 text-white px-4 py-2 rounded shadow fade-in">
          {toast}
        </div>
      )}

      <main className="max-w-6xl mx-auto p-8 grid md:grid-cols-3 gap-8">
        {/* Restaurants */}
        <div className="md:col-span-2">
          <h2 className="font-bold mb-4">Available Restaurants</h2>
          <div className="grid sm:grid-cols-2 gap-4">
            {restaurants.map(r => (
              <div key={r.id} className="bg-white p-6 rounded-xl shadow hover:scale-105 transition">
                <h3 className="font-bold">{r.name}</h3>
                <p className="text-sm mb-3">üìç {r.country}</p>
                <button onClick={() => addOrder(r.id, "Biryani")}
                  className="mr-2 bg-orange-500 text-white px-4 py-2 rounded">
                  Biryani
                </button>
                <button onClick={() => addOrder(r.id, "Pizza")}
                  className="border px-4 py-2 rounded">
                  Pizza
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Cart */}
        <div className="space-y-6">
          <div className="bg-white p-6 rounded-xl shadow">
            <h2 className="font-bold mb-3">Your Orders</h2>
            {orders.map(o => (
              <div key={o.id} className="flex justify-between text-sm">
                <span>{o.item} √ó {o.qty}</span>
                <span>‚Çπ{o.total}</span>
              </div>
            ))}

            <button onClick={async () => {
              await checkoutOrder(user);
              setOrders([]);
              showToast("Order placed successfully");
            }}
              className="w-full bg-green-600 text-white py-2 mt-4 rounded">
              Checkout
            </button>

            <button onClick={() => { cancelOrder(user); setOrders([]); }}
              className="w-full text-red-500 mt-2">
              Cancel All
            </button>
          </div>

          <div className="bg-white p-6 rounded-xl shadow">
            <h2 className="font-bold mb-2">Payment</h2>
            <select className="w-full border p-2 mb-2"
              onChange={e => setPaymentMethod(e.target.value)}>
              <option>Select</option>
              <option>UPI</option>
              <option>Card</option>
              <option>Cash</option>
            </select>
            <button onClick={() => { updatePayment(user, paymentMethod); showToast("Payment updated"); }}
              className="w-full border py-2">
              Update Payment
            </button>
          </div>

          {/* ADMIN ONLY */}
          {user === "nick" && <AdminDashboard orders={orders} />}
        </div>
      </main>
    </div>
  );
}

export default App;
