import { useState, useEffect } from "react";
import {
  login,
  getMenu,
  addOrder,
  cancelOrder,
  checkout,
  adminOrders,
  addPaymentMethod,
} from "./api";

import Login from "./components/login";
import Navbar from "./components/Navbar";
import RestaurantList from "./components/RestaurantList";
import Cart from "./components/Cart";
import AdminDashboard from "./components/AdminDashboard";

export default function App() {
  const [token, setToken] = useState("");
  const [role, setRole] = useState("");
  const [country, setCountry] = useState("");
  const [menu, setMenu] = useState({});
  const [orders, setOrders] = useState([]);
  const [payment, setPayment] = useState("CREDIT_CARD");
  const [newPayment, setNewPayment] = useState("");

  // ---------- LOGIN ----------
  const handleLogin = async (username) => {
    const res = await login(username);
    setToken(res.token);
    setRole(res.role);
    setCountry(res.country);

    const menuData = await getMenu(res.token);
    setMenu(menuData);
  };

  // ---------- FETCH ORDERS ----------
  useEffect(() => {
    if (!token) return;

    const interval = setInterval(() => {
      if (role === "ADMIN") {
        adminOrders(token).then(setOrders);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [token, role]);

  // ---------- UI ----------
  if (!token) return <Login onLogin={handleLogin} />;

  return (
    <>
      <Navbar role={role} country={country} logout={() => window.location.reload()} />

      <div className="p-6 bg-slate-100 min-h-screen">
        {/* ADMIN DASHBOARD */}
        {role === "ADMIN" && (
          <AdminDashboard orders={orders} />
        )}

        <div className="grid grid-cols-3 gap-6">
          {/* RESTAURANTS */}
          <div className="col-span-2">
            <RestaurantList
              menu={menu}
              onAdd={(item, price) => addOrder(token, item, price)}
            />
          </div>

          {/* CART + CHECKOUT */}
          <div>
            <Cart
              orders={orders.filter(o => o.status === "CREATED")}
              onCancel={(id) => cancelOrder(id, token)}
            />

            {/* CHECKOUT */}
            {role !== "MEMBER" && (
              <div className="bg-white p-4 rounded-xl shadow mt-4 fade-in">
                <select
                  className="border p-2 w-full rounded mb-2"
                  value={payment}
                  onChange={(e) => setPayment(e.target.value)}
                >
                  <option value="CREDIT_CARD">Credit Card</option>
                  <option value="UPI">UPI</option>
                  <option value="CASH">Cash</option>
                </select>

                <button
                  onClick={() => checkout(token, payment)}
                  className="w-full bg-green-500 hover:bg-green-600 text-white py-2 rounded"
                >
                  Checkout
                </button>
              </div>
            )}

            {/* ADMIN PAYMENT MANAGEMENT */}
            {role === "ADMIN" && (
              <div className="bg-white p-4 rounded-xl shadow mt-4 fade-in">
                <h3 className="font-bold mb-2 text-orange-600">
                  Add Payment Method
                </h3>

                <input
                  type="text"
                  placeholder="New payment method"
                  className="border p-2 w-full rounded mb-2"
                  value={newPayment}
                  onChange={(e) => setNewPayment(e.target.value)}
                />

                <button
                  onClick={() => {
                    if (newPayment.trim()) {
                      addPaymentMethod(token, newPayment.trim());
                      setNewPayment("");
                    }
                  }}
                  className="w-full bg-orange-500 hover:bg-orange-600 text-white py-2 rounded"
                >
                  Add Method
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}
