import { useEffect, useState } from "react";
import { getAdminOrders } from "./api";

export default function AdminDashboard({ user }) {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    getAdminOrders(user).then(setOrders);
  }, [user]);

  return (
    <div className="bg-white p-6 rounded-xl shadow border mt-6">
      <h2 className="font-bold mb-4">Admin Orders Dashboard</h2>

      {orders.length === 0 ? (
        <p className="text-gray-400">No orders yet</p>
      ) : (
        <table className="w-full text-sm">
          <thead>
            <tr className="border-b">
              <th>User</th>
              <th>Item</th>
              <th>Qty</th>
              <th>Total</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(o => (
              <tr key={o.id} className="border-b">
                <td>{o.user}</td>
                <td>{o.item}</td>
                <td className="text-center">{o.qty}</td>
                <td className="text-center">â‚¹{o.total}</td>
                <td className="text-center">{o.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
