import { useEffect, useState } from "react";
import { getAdminOrders } from "./api";

export default function AdminDashboard({ user }) {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const load = () => getAdminOrders(user).then(setOrders);
    load();
    const i = setInterval(load, 2000);
    return () => clearInterval(i);
  }, [user]);

  return (
    <div className="bg-white rounded-xl shadow p-6 mt-6">
      <h2 className="font-bold text-lg mb-4">Admin Orders Dashboard</h2>

      <table className="w-full text-sm">
        <thead className="bg-gray-100">
          <tr>
            <th>User</th>
            <th>Item</th>
            <th>Qty</th>
            <th>Total</th>
            <th>Payment</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(o => (
            <tr key={o.id} className="border-b text-center">
              <td>{o.user}</td>
              <td>{o.item}</td>
              <td>{o.qty}</td>
              <td className="text-green-600">â‚¹{o.total}</td>
              <td>{o.payment_method || "-"}</td>
              <td className="font-semibold text-green-600">{o.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
