const BASE = "http://localhost:8000";

export const login = (role) =>
  fetch(`${BASE}/login?role=${role}`, { method: "POST" })
    .then(r => r.json());

export const getMenu = (token) =>
  fetch(`${BASE}/menu`, {
    headers: { token }
  }).then(r => r.json());
