import { useState, useEffect } from "react";

const images = [
  "https://images.unsplash.com/photo-1600891964599-f61ba0e24092?auto=format&fit=crop&w=1470&q=80",
  "https://images.unsplash.com/photo-1606756790138-261d2b21cd75?auto=format&fit=crop&w=1470&q=80",
  "https://images.unsplash.com/photo-1540189549336-e6e99c3679fe?auto=format&fit=crop&w=1470&q=80",
];

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [index, setIndex] = useState(0);

  // Background image slider
  useEffect(() => {
    const i = setInterval(() => {
      setIndex((prev) => (prev + 1) % images.length);
    }, 5000);
    return () => clearInterval(i);
  }, []);

  return (
    <div className="relative h-screen w-full overflow-hidden font-sans">
      {/* BACKGROUND IMAGE */}
      <div
        className="absolute inset-0 bg-cover bg-center scale-105 transition-all duration-1000"
        style={{ backgroundImage: `url(${images[index]})` }}
      />

      {/* DARK GRADIENT OVERLAY (IMAGE STILL VISIBLE) */}
      <div className="absolute inset-0 bg-gradient-to-br from-black/60 via-black/40 to-black/60" />

      {/* LOGIN CARD */}
      <div className="relative z-10 flex items-center justify-center h-full px-4">
        <div className="w-full max-w-md p-8 rounded-3xl shadow-2xl backdrop-blur-lg bg-white/90 animate-fade-in">
          {/* TITLE */}
          <h1 className="text-4xl font-extrabold text-indigo-600 text-center mb-2">
            FoodDash 斤
          </h1>
          <p className="text-center text-gray-600 mb-6">
            Order food instantly, delivered fresh
          </p>

          {/* INPUT */}
          <input
            className="w-full p-3 rounded-lg border border-gray-300 mb-4
                       focus:outline-none focus:ring-2 focus:ring-indigo-500 transition"
            placeholder="Username (nick, marvel, thanos...)"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          {/* LOGIN BUTTON */}
          <button
            onClick={() => onLogin(username)}
            className="w-full bg-indigo-600 hover:bg-indigo-700
                       text-white py-3 rounded-lg font-semibold
                       transition transform hover:scale-105"
          >
            Login
          </button>

          {/* DEMO USERS */}
          <div className="text-xs text-gray-500 mt-4 text-center">
            Demo users:{" "}
            <span className="font-medium">nick</span> 路{" "}
            <span className="font-medium">marvel</span> 路{" "}
            <span className="font-medium">thanos</span> 路{" "}
            <span className="font-medium">thor</span> 路{" "}
            <span className="font-medium">travis</span>
          </div>
        </div>
      </div>
    </div>
  );
}
