const images = {
  Biryani:
    "https://images.unsplash.com/photo-1626777552726-4a6b54c97e46",
  Burger:
    "https://images.unsplash.com/photo-1550547660-d9450f859349",
};

export default function RestaurantList({ menu, onAdd }) {
  return Object.entries(menu).map(([country, items]) => (
    <div key={country} className="bg-white p-4 rounded-xl shadow fade-in mb-6">
      <h3 className="font-bold mb-3">üìç {country}</h3>
      {items.map(i => (
        <div
          key={i.item}
          className="flex items-center gap-4 border rounded-lg p-2 mb-2 hover:shadow transition"
        >
          <img
            src={images[i.item]}
            className="w-16 h-16 rounded object-cover"
          />
          <div className="flex-1">
            <p className="font-semibold">{i.item}</p>
            <p className="text-sm text-gray-500">‚Çπ{i.price}</p>
          </div>
          <button
            onClick={() => onAdd(i.item, i.price)}
            className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-1 rounded"
          >
            Add
          </button>
        </div>
      ))}
    </div>
  ));
}
