const images = {
  Biryani:
    "https://images.unsplash.com/photo-1626777552726-4a6b54c97e46",
  Burger:
    "https://images.unsplash.com/photo-1550547660-d9450f859349",
};

export default function RestaurantList({ menu, onAdd }) {
  return Object.entries(menu).map(([country, items]) => (
    <div
      key={country}
      className="bg-white p-5 rounded-2xl shadow-sm mb-6 fade-in"
    >
      <h3 className="font-semibold mb-4 text-[color:var(--primary)]">
        üìç {country}
      </h3>

      {items.map(i => (
        <div
          key={i.item}
          className="flex items-center gap-4 border border-gray-100
                     rounded-xl p-3 mb-3 hover:shadow transition"
        >
          <img
            src={images[i.item]}
            className="w-16 h-16 rounded-lg object-cover"
          />

          <div className="flex-1">
            <p className="font-semibold">{i.item}</p>
            <p className="text-sm text-gray-500">‚Çπ{i.price}</p>
          </div>

          <button
            onClick={() => onAdd(i.item, i.price)}
            className="bg-[color:var(--primary)]
                       hover:bg-[#239a91]
                       text-white px-4 py-1.5 rounded-lg text-sm"
          >
            Add
          </button>
        </div>
      ))}
    </div>
  ));
}
