const BASE = "http://localhost:8000";

export const getRestaurants = (u) =>
  fetch(`${BASE}/restaurants?username=${u}`).then(r => r.json());

export const createOrder = (u, id, item) =>
  fetch(`${BASE}/order?username=${u}&restaurant_id=${id}&item=${item}`, { method: "POST" });

export const checkoutOrder = (u) =>
  fetch(`${BASE}/checkout?username=${u}`, { method: "POST" });

export const updatePayment = (u, m) =>
  fetch(`${BASE}/payment?username=${u}&method=${m}`, { method: "POST" });

export const getAdminOrders = (u) =>
  fetch(`${BASE}/admin/orders?username=${u}`).then(r => r.json());
