export default function OrderCard({ order, highlight }) {
  const colors = {
    CREATED: "bg-yellow-100",
    PAID: "bg-blue-100",
    DELIVERED: "bg-green-100"
  };

  return (
    <div
      className={`p-3 rounded mb-2 transition-all duration-700 ${
        colors[order.status]
      } ${highlight ? "ring-4 ring-orange-400 scale-105" : ""}`}
    >
      <div className="flex justify-between">
        <span>{order.item}</span>
        <span className="font-bold">{order.status}</span>
      </div>
    </div>
  );
}
