import { useState, useEffect } from "react";
import Login from "./login";
import { getRestaurants, createOrder, checkoutOrder, cancelOrder, updatePayment } from "./api";

function App() {
  const [user, setUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [orders, setOrders] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (user) {
      fetchData();
    }
  }, [user]);

  async function fetchData() {
    setLoading(true);
    const data = await getRestaurants(user);
    setRestaurants(data);
    setLoading(false);
  }

  const handleAction = async (actionFn, ...args) => {
    const result = await actionFn(user, ...args);
    if (result.error) {
      alert(`Error: ${result.message}`);
    } else {
      alert(result.message);
      return result;
    }
  };

  const handleOrder = async (restaurant_id, item) => {
    const result = await createOrder(user, restaurant_id, item);
    if (result.message === "Order created") {
      setOrders([...orders, { restaurant_id, item, status: "CREATED", id: Date.now() }]);
    } else {
      alert("Unauthorized to create orders.");
    }
  };

  if (!user) return <Login setUser={setUser} />;

  return (
    <div className="min-h-screen bg-gray-50 font-sans text-gray-900">
      {/* Navbar */}
      <nav className="bg-white shadow-sm border-b px-8 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-orange-600">FoodDash</h1>
        <div className="flex items-center gap-4">
          <span className="bg-orange-100 text-orange-700 px-3 py-1 rounded-full text-sm font-medium">
            Role: {user.toUpperCase()}
          </span>
          <button 
            onClick={() => setUser(null)}
            className="text-gray-500 hover:text-red-600 font-medium transition"
          >
            Logout
          </button>
        </div>
      </nav>

      <main className="max-w-6xl mx-auto p-8 grid grid-cols-1 md:grid-cols-3 gap-8">
        
        {/* Restaurant Section */}
        <div className="md:col-span-2 space-y-6">
          <h2 className="text-xl font-semibold border-b pb-2">Available Restaurants</h2>
          {loading ? <p>Loading...</p> : (
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {restaurants.map((r) => (
                <div key={r.id} className="bg-white p-6 rounded-xl shadow-sm border hover:shadow-md transition">
                  <h3 className="text-lg font-bold">{r.name}</h3>
                  <p className="text-gray-500 text-sm mb-4">üìç {r.country}</p>
                  <div className="flex gap-2">
                    <button 
                      onClick={() => handleOrder(r.id, "Biryani")}
                      className="flex-1 bg-orange-500 text-white py-2 rounded-lg hover:bg-orange-600 text-sm"
                    >
                      Order Biryani
                    </button>
                    <button 
                      onClick={() => handleOrder(r.id, "Pizza")}
                      className="flex-1 border border-orange-500 text-orange-500 py-2 rounded-lg hover:bg-orange-50 text-sm"
                    >
                      Order Pizza
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Sidebar: Cart & Payment */}
        <div className="space-y-6">
          <div className="bg-white p-6 rounded-xl shadow-sm border">
            <h2 className="text-lg font-bold mb-4">Your Orders</h2>
            {orders.length === 0 ? (
              <p className="text-gray-400 text-center py-4">Cart is empty</p>
            ) : (
              <ul className="space-y-3 mb-6">
                {orders.map((o) => (
                  <li key={o.id} className="flex justify-between text-sm border-b pb-2">
                    <span>{o.item}</span>
                    <span className="text-green-600 font-bold">CREATED</span>
                  </li>
                ))}
              </ul>
            )}
            <div className="flex flex-col gap-2">
              <button 
                onClick={async () => { await handleAction(checkoutOrder); setOrders([]); }}
                className="w-full bg-green-600 text-white py-3 rounded-lg font-bold hover:bg-green-700 disabled:bg-gray-300"
                disabled={orders.length === 0}
              >
                Checkout
              </button>
              <button 
                onClick={async () => { await handleAction(cancelOrder); setOrders([]); }}
                className="w-full text-red-500 text-sm hover:underline"
                disabled={orders.length === 0}
              >
                Cancel All
              </button>
            </div>
          </div>

          <div className="bg-white p-6 rounded-xl shadow-sm border">
            <h2 className="text-lg font-bold mb-2">Payment</h2>
            <select
              className="w-full border p-2 rounded-lg mb-4 bg-gray-50"
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
            >
              <option value="">Select Method</option>
              <option value="UPI">UPI</option>
              <option value="Credit Card">Credit Card</option>
              <option value="Cash">Cash</option>
            </select>
            <button
              onClick={() => handleAction(updatePayment, paymentMethod)}
              className="w-full border border-gray-300 py-2 rounded-lg hover:bg-gray-50"
            >
              Update Payment
            </button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
