import { useState, useEffect } from "react";

const images = [
  "https://images.unsplash.com/photo-1600891964599-f61ba0e24092",
  "https://images.unsplash.com/photo-1606756790138-261d2b21cd75",
  "https://images.unsplash.com/photo-1540189549336-e6e99c3679fe",
];

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const i = setInterval(() => {
      setIndex(p => (p + 1) % images.length);
    }, 5000);
    return () => clearInterval(i);
  }, []);

  return (
    <div className="relative h-screen overflow-hidden">
      {/* Background image */}
      <div
        className="absolute inset-0 bg-cover bg-center scale-105"
        style={{ backgroundImage: `url(${images[index]})` }}
      />

      {/* Cream overlay */}
      <div className="absolute inset-0 bg-[color:var(--cream)]/80" />

      {/* Login Card */}
      <div className="relative z-10 flex items-center justify-center h-full px-4">
        <div className="bg-white rounded-3xl p-8 w-full max-w-md warm-shadow fade-in">
          <h1 className="text-4xl font-extrabold text-center mb-2 text-[color:var(--sage)]">
            FoodDash 斤
          </h1>

          <p className="text-center text-gray-600 mb-6">
            Fresh food, delivered warm
          </p>

          <input
            className="w-full p-3 rounded-lg border border-gray-300 mb-4
                       focus:ring-2 focus:ring-[color:var(--sage)]"
            placeholder="Username (nick, marvel, thanos...)"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          <button
            onClick={() => onLogin(username)}
            className="w-full bg-[color:var(--terracotta)]
                       hover:opacity-90 text-white py-3 rounded-lg
                       font-semibold transition"
          >
            Login
          </button>

          <div className="text-xs text-gray-500 mt-4 text-center">
            Demo users: nick 路 marvel 路 thanos 路 thor 路 travis
          </div>
        </div>
      </div>
    </div>
  );
}
