import { useState, useEffect } from "react";
import Login from "./login";
import AdminDashboard from "./AdminDashboard";
import { getRestaurants, createOrder, checkoutOrder, cancelOrder } from "./api";

function App() {
  const [user, setUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    if (user) getRestaurants(user).then(setRestaurants);
  }, [user]);

  if (!user) return <Login setUser={setUser} />;

  return (
    <div>
      <nav className="bg-white p-4 shadow flex justify-between">
        <h1 className="font-bold text-orange-600">FoodDash</h1>
        <button onClick={() => setUser(null)}>Logout</button>
      </nav>

      <div className="p-8 grid md:grid-cols-3 gap-6">
        <div className="md:col-span-2">
          {restaurants.map(r => (
            <div key={r.id} className="bg-white p-4 mb-3 rounded shadow">
              <h3>{r.name}</h3>
              <button onClick={() => {
                createOrder(user, r.id, "Biryani");
                setCart([...cart, "Biryani"]);
              }}>
                Order Biryani
              </button>
            </div>
          ))}
        </div>

        <div>
          <h3>Cart</h3>
          {cart.map((c, i) => <p key={i}>{c}</p>)}

          <button onClick={() => {
            checkoutOrder(user);
            setCart([]);
          }}>
            Checkout
          </button>

          <button onClick={() => {
            cancelOrder(user);
            setCart([]);
          }}>
            Cancel
          </button>

          {user === "nick" && <AdminDashboard user={user} />}
        </div>
      </div>
    </div>
  );
}

export default App;
