import { useState } from "react";

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");

  return (
    <div className="h-screen flex items-center justify-center bg-[color:var(--bg)]">
      <div className="card p-8 w-full max-w-md animate-fadeIn">
        <h1 className="text-3xl font-bold text-center mb-6 text-[color:var(--secondary)]">
          FoodDash üçî
        </h1>

        <input
          className="w-full border p-3 rounded-lg mb-4 focus:ring-2 focus:ring-[color:var(--secondary)]"
          placeholder="Enter username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <button
          onClick={() => onLogin(username)}
          className="btn-primary w-full"
        >
          Login
        </button>

        <p className="text-xs text-gray-500 text-center mt-4">
          Demo users: admin, manager, member
        </p>
      </div>
    </div>
  );
}
