import { useEffect, useState } from "react";
import {
  getMenu,
  addOrder,
  getOrders,
  cancelOrder,
  payOrder,
} from "./api";

export default function App() {
  const [user, setUser] = useState(null);
  const [menu, setMenu] = useState({});
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    if (!user) return;
    getMenu(user).then(setMenu);
    const i = setInterval(() => getOrders(user).then(setOrders), 1000);
    return () => clearInterval(i);
  }, [user]);

  if (!user) {
    return (
      <div style={{ padding: 40 }}>
        <h2>üçΩ Food Ordering Login</h2>
        {["nick","marvel","america","thanos","thor","travis"].map(u =>
          <button key={u} onClick={() => setUser(u)}>{u}</button>
        )}
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Welcome {user}</h2>

      {Object.entries(menu).map(([c, items]) => (
        <div key={c}>
          <h3>üìç {c}</h3>
          {items.map(i => (
            <div key={i.id}>
              <img src={i.img} width="120" />
              <b>{i.name}</b> ‚Çπ{i.price}
              <button onClick={() => addOrder(user, i.id)}>
                Add
              </button>
            </div>
          ))}
        </div>
      ))}

      <h3>üßæ Orders</h3>
      {orders.map(o => (
        <div key={o.id}>
          {o.item} ‚Äì ‚Çπ{o.price} ‚Äì {o.status}
          <button onClick={() => cancelOrder(user, o.id)}>Cancel</button>
          {user === "nick" && (
            <button onClick={() => payOrder(user, o.id, "CREDIT_CARD")}>
              Pay
            </button>
          )}
        </div>
      ))}
    </div>
  );
}
