import { useState } from "react";
import loginImage from "../assets/food_login.jpg"; // static background image

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [role, setRole] = useState("MEMBER");

  const roleUsers = {
    ADMIN: "nick",
    MANAGER: "marvel",
    MEMBER: "thanos",
  };

  const handleLogin = () => {
    const user = username || roleUsers[role];
    onLogin(user);
  };

  return (
    <div className="relative h-screen w-full overflow-hidden">
      {/* BACKGROUND IMAGE */}
      <div
        className="absolute inset-0 bg-cover bg-center"
        style={{ backgroundImage: `url(${loginImage})` }}
      />
      <div className="absolute inset-0 bg-orange-600/70" />

      <div className="relative z-10 flex items-center justify-center h-full">
        <div className="bg-white w-96 p-8 rounded-2xl shadow-2xl fade-in">
          <h1 className="text-3xl font-bold text-orange-600 text-center mb-1">
            FoodDash üçî
          </h1>
          <p className="text-center text-gray-500 mb-6">
            Login to order delicious food
          </p>

          <select
            className="border w-full p-3 rounded-lg mb-3 focus:ring-2 focus:ring-orange-400"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="ADMIN">Admin</option>
            <option value="MANAGER">Manager</option>
            <option value="MEMBER">Member</option>
          </select>

          <input
            className="border w-full p-3 rounded-lg mb-4 focus:ring-2 focus:ring-orange-400"
            placeholder="Optional: override username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          <button
            onClick={handleLogin}
            className="w-full bg-orange-500 hover:bg-orange-600 text-white py-3 rounded-lg font-semibold transition"
          >
            Login
          </button>

          <div className="text-xs text-gray-400 mt-4 text-center">
            Default users: Admin ‚Üí nick, Manager ‚Üí marvel, Member ‚Üí thanos
          </div>
        </div>
      </div>
    </div>
  );
}
