import { useEffect, useState } from "react";
import Login from "./login";
import AdminDashboard from "./AdminDashboard";
import {
  getRestaurants, createOrder,
  checkoutOrder, cancelOrder, updatePayment
} from "./api";

function App() {
  const [user, setUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [toast, setToast] = useState("");

  useEffect(() => {
    if (user) getRestaurants(user).then(setRestaurants);
  }, [user]);

  const notify = msg => {
    setToast(msg);
    setTimeout(() => setToast(""), 2000);
  };

  if (!user) return <Login setUser={setUser} />;

  return (
    <div>
      {toast && (
        <div className="fixed top-6 right-6 bg-green-500 text-white px-4 py-2 rounded">
          {toast}
        </div>
      )}

      <nav className="bg-white shadow px-6 py-4 flex justify-between">
        <h1 className="font-bold text-orange-600">FoodDash</h1>
        <button onClick={() => setUser(null)}>Logout</button>
      </nav>

      <main className="p-8 grid md:grid-cols-3 gap-6">
        <div className="md:col-span-2">
          {restaurants.map(r => (
            <div key={r.id} className="bg-white p-6 mb-4 rounded-xl shadow">
              <h3 className="font-bold">{r.name}</h3>
              <button
                onClick={() => {
                  createOrder(user, r.id, "Biryani");
                  notify("Item added");
                }}
                className="mt-2 bg-orange-500 text-white px-4 py-2 rounded"
              >
                Add Biryani
              </button>
            </div>
          ))}
        </div>

        <div>
          <button onClick={() => {
            checkoutOrder(user);
            notify("Order paid");
          }} className="btn w-full bg-green-600">Checkout</button>

          {user === "nick" && (
            <>
              <button onClick={() => updatePayment(user, "UPI")}
                className="btn w-full bg-blue-600 mt-2">
                Update Payment
              </button>
              <AdminDashboard user={user} />
            </>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
