const images = {
  Biryani: "https://images.unsplash.com/photo-1626777552726-4a6b54c97e46",
  Burger: "https://images.unsplash.com/photo-1550547660-d9450f859349",
};

export default function RestaurantList({ menu, onAdd }) {
  return Object.entries(menu).map(([country, items]) => (
    <div
      key={country}
      className="bg-white rounded-2xl p-5 mb-6 warm-shadow fade-in"
    >
      <h3 className="font-semibold mb-4 text-[color:var(--sage)]">
        üìç {country}
      </h3>

      {items.map(i => (
        <div
          key={i.item}
          className="flex items-center gap-4 p-3 mb-3
                     rounded-xl bg-[color:var(--cream)]
                     hover:shadow-md transition"
        >
          <img
            src={images[i.item]}
            className="w-16 h-16 rounded-lg object-cover warm-shadow"
          />

          <div className="flex-1">
            <p className="font-semibold">{i.item}</p>
            <p className="text-sm font-medium text-[color:var(--terracotta)]">
              ‚Çπ{i.price}
            </p>
          </div>

          <button
            onClick={() => onAdd(i.item, i.price)}
            className="bg-[color:var(--terracotta)]
                       text-white px-4 py-1.5 rounded-lg text-sm
                       hover:opacity-90"
          >
            Add
          </button>
        </div>
      ))}
    </div>
  ));
}
