export default function Cart({ orders, onCancel, payment, setPayment, handleCheckout }) {
  return (
    <div className="card p-4 sticky top-4">
      <h3 className="font-bold mb-3 text-[color:var(--secondary)]">ðŸ›’ Cart</h3>

      {orders.length === 0 && <p className="text-sm text-gray-400">Empty</p>}

      {orders.map(o => (
        <div key={o.id} className="flex justify-between py-2">
          <span>{o.item}</span>
          <span className="font-bold text-[color:var(--primary)]">â‚¹{o.total}</span>
          <button onClick={() => onCancel(o.id)} className="text-red-500 text-sm">Remove</button>
        </div>
      ))}

      {orders.length > 0 && (
        <div className="mt-4">
          <select
            className="border p-2 w-full rounded mb-2"
            value={payment}
            onChange={(e) => setPayment(e.target.value)}
          >
            <option>CREDIT_CARD</option>
            <option>UPI</option>
            <option>CASH</option>
          </select>
          <button onClick={handleCheckout} className="btn-primary w-full mt-2">
            Checkout
          </button>
        </div>
      )}
    </div>
  );
}
