const BASE = "http://localhost:8000";

export const login = async (u) =>
  (await fetch(`${BASE}/login?username=${u}`, { method: "POST" })).json();

export const createOrder = (token, item) =>
  fetch(`${BASE}/order?item=${item}`, {
    method: "POST",
    headers: { token }
  });

export const checkout = (token) =>
  fetch(`${BASE}/checkout`, { method: "POST", headers: { token } });

export const cancelOrder = (id, token) =>
  fetch(`${BASE}/order/${id}`, { method: "DELETE", headers: { token } });

export const adminOrders = (token) =>
  fetch(`${BASE}/admin/orders`, { headers: { token } }).then(r => r.json());
