const BASE = "http://localhost:8000";

export const getMenu = (user) =>
  fetch(`${BASE}/menu`, { headers: { "X-USER": user } }).then(r => r.json());

export const addOrder = (user, id) =>
  fetch(`${BASE}/order?item_id=${id}`, {
    method: "POST",
    headers: { "X-USER": user },
  }).then(r => r.json());

export const cancelOrder = (user, id) =>
  fetch(`${BASE}/cancel/${id}`, {
    method: "POST",
    headers: { "X-USER": user },
  });

export const payOrder = (user, id, method) =>
  fetch(`${BASE}/pay/${id}?method=${method}`, {
    method: "POST",
    headers: { "X-USER": user },
  }).then(r => r.json());

export const getOrders = (user) =>
  fetch(`${BASE}/orders`, { headers: { "X-USER": user } }).then(r => r.json());
