import { useEffect, useState } from "react";
import Login from "./login";
import Navbar from "./Navbar";
import Hero from "./Hero";
import AdminDashboard from "./AdminDashboard";
import {
  getRestaurants,
  createOrder,
  checkoutOrder,
  updatePayment,
} from "./api";

function App() {
  const [user, setUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [toast, setToast] = useState("");

  useEffect(() => {
    if (user) getRestaurants(user).then(setRestaurants);
  }, [user]);

  const notify = (msg) => {
    setToast(msg);
    setTimeout(() => setToast(""), 2000);
  };

  if (!user) return <Login setUser={setUser} />;

  return (
    <div className="min-h-screen bg-gray-50">

      {toast && (
        <div className="fixed top-6 right-6 bg-green-500 text-white px-4 py-2 rounded shadow">
          {toast}
        </div>
      )}

      <Navbar user={user} onLogout={() => setUser(null)} />
      <Hero />

      <main className="max-w-7xl mx-auto px-6 py-10 grid md:grid-cols-3 gap-8">

        {/* Restaurants */}
        <section className="md:col-span-2 space-y-6">
          <h2 className="text-2xl font-bold text-gray-800">
            Popular Restaurants
          </h2>

          <div className="grid sm:grid-cols-2 gap-6">
            {restaurants.map((r) => (
              <div
                key={r.id}
                className="bg-white rounded-2xl shadow hover:shadow-lg transition p-6"
              >
                <h3 className="font-bold text-lg">{r.name}</h3>
                <p className="text-sm text-gray-500 mb-4">
                  üìç {r.country}
                </p>

                <button
                  onClick={() => {
                    createOrder(user, r.id, "Biryani");
                    notify("Biryani added to cart");
                  }}
                  className="w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded-lg transition"
                >
                  Add Biryani ‚Ä¢ ‚Çπ250
                </button>
              </div>
            ))}
          </div>
        </section>

        {/* Sidebar */}
        <aside className="bg-white rounded-2xl shadow p-6 space-y-4">
          <h3 className="text-lg font-bold">Payment</h3>

          {user === "nick" && (
            <>
              <select
                value={paymentMethod}
                onChange={(e) => setPaymentMethod(e.target.value)}
                className="w-full border rounded-lg p-2 bg-gray-50"
              >
                <option value="">Select payment method</option>
                <option value="UPI">UPI</option>
                <option value="CREDIT_CARD">Credit Card</option>
                <option value="COD">Cash on Delivery</option>
              </select>

              <button
                onClick={async () => {
                  if (!paymentMethod) return notify("Select payment method");
                  await updatePayment(user, paymentMethod);
                  notify(`Payment set to ${paymentMethod}`);
                }}
                className="w-full bg-blue-600 text-white py-2 rounded-lg"
              >
                Update Payment
              </button>
            </>
          )}

          <button
            onClick={async () => {
              await checkoutOrder(user);
              notify("Payment successful");
            }}
            className="w-full bg-green-600 hover:bg-green-700 text-white py-3 rounded-lg font-bold"
          >
            Checkout
          </button>

          {user === "nick" && <AdminDashboard user={user} />}
        </aside>
      </main>
    </div>
  );
}

export default App;
