import { useState, useEffect, useRef } from "react";
import { login, getMenu, addOrder, cancelOrder, checkout, adminOrders, myOrders } from "./api";

import Login from "./components/login";
import Navbar from "./components/Navbar";
import RestaurantList from "./components/RestaurantList";
import Cart from "./components/Cart";
import AdminDashboard from "./components/AdminDashboard";
import OrderHistory from "./components/OrderHistory";

export default function App() {
  const [token, setToken] = useState("");
  const [role, setRole] = useState("");
  const [menu, setMenu] = useState({});
  const [orders, setOrders] = useState([]);
  const [payment, setPayment] = useState("CREDIT_CARD");
  const [selectedUser, setSelectedUser] = useState("");

  const historyRef = useRef(null);

  const handleLogin = async (u) => {
    const r = await login(u);
    setToken(r.token);
    setRole(r.role);
    setMenu(await getMenu(r.token));
    if (r.role === "ADMIN") setOrders([]);
    else setOrders(await myOrders(r.token));
  };

  const handleAddOrder = async (item, price) => {
    if (!selectedUser && role === "ADMIN") {
      alert("Select a user first");
      return;
    }
    await addOrder(token, item, price, selectedUser);
    if (role === "ADMIN") setOrders(await adminOrders(token));
    else setOrders(await myOrders(token));
  };

  const handleCancelOrder = async (id) => {
    await cancelOrder(id, token);
    if (role === "ADMIN") setOrders(await adminOrders(token));
    else setOrders(await myOrders(token));
  };

  const handleCheckout = async () => {
    await checkout(token, payment, selectedUser);
    if (role === "ADMIN") setOrders(await adminOrders(token));
    else setOrders(await myOrders(token));
  };

  useEffect(() => {
    if (role === "ADMIN") {
      const interval = setInterval(() => {
        adminOrders(token).then(setOrders);
      }, 2000); // optional auto-update
      return () => clearInterval(interval);
    }
  }, [role, token]);

  if (!token) return <Login onLogin={handleLogin} />;

  return (
    <>
      <Navbar role={role} logout={() => window.location.reload()} />

      <div className="p-6 bg-[#FDF5E6] min-h-screen">
        {role === "ADMIN" && <AdminDashboard orders={orders} />}

        {role === "ADMIN" && (
          <div className="bg-white p-4 rounded-xl shadow mb-4">
            <label className="font-semibold mb-2 block">Select User:</label>
            <select
              className="border p-2 w-full rounded"
              value={selectedUser}
              onChange={(e) => setSelectedUser(e.target.value)}
            >
              <option value="">-- Select --</option>
              <option value="marvel">marvel</option>
              <option value="nick">nick</option>
              <option value="travis">travis</option>
              <option value="thor">thor</option>
              <option value="thanos">thanos</option>
            </select>
          </div>
        )}

        <div className="grid grid-cols-3 gap-6">
          <div className="col-span-2">
            <RestaurantList menu={menu} onAdd={handleAddOrder} />
          </div>

          <div className="overflow-y-auto max-h-[70vh]">
            {role !== "ADMIN" && (
              <Cart orders={orders.filter(o => o.status === "CREATED")} onCancel={handleCancelOrder} />
            )}

            {role !== "MEMBER" && (
              <div className="bg-white p-4 rounded-xl shadow mt-4">
                <select
                  className="border p-2 w-full rounded mb-2"
                  onChange={(e) => setPayment(e.target.value)}
                  value={payment}
                >
                  <option>CREDIT_CARD</option>
                  <option>UPI</option>
                  <option>CASH</option>
                </select>
                <button
                  onClick={handleCheckout}
                  className="w-full bg-[#E2725B] hover:bg-[#C05A48] text-white py-2 rounded font-semibold transition transform hover:scale-105"
                >
                  Checkout
                </button>
              </div>
            )}

            <div ref={historyRef}>
              <OrderHistory orders={orders.filter(o => o.status !== "CREATED")} />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
