import { useEffect, useState } from "react";
import { login, createOrder, cancelOrder, checkout, adminOrders } from "./api";
import { PieChart, Pie, Cell } from "recharts";

export default function App() {
  const [token, setToken] = useState("");
  const [orders, setOrders] = useState([]);
  const [toast, setToast] = useState("");

  const notify = msg => {
    setToast(msg);
    setTimeout(() => setToast(""), 2000);
  };

  useEffect(() => {
    if (token) {
      const i = setInterval(() => {
        adminOrders(token).then(setOrders);
      }, 2000);
      return () => clearInterval(i);
    }
  }, [token]);

  if (!token) {
    return (
      <div className="h-screen flex justify-center items-center">
        <button
          onClick={async () => {
            const r = await login("nick");
            setToken(r.token);
          }}
          className="px-6 py-3 bg-indigo-200 rounded-xl"
        >
          Login as Admin
        </button>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-6xl mx-auto">

      {toast && (
        <div className="fixed top-6 right-6 bg-green-300 px-4 py-2 rounded">
          {toast}
        </div>
      )}

      <h1 className="text-3xl font-semibold mb-6">FoodDash</h1>

      <div className="grid grid-cols-3 gap-6">

        {/* Restaurant */}
        <div className="bg-white p-6 rounded-xl">
          <img src="https://images.unsplash.com/photo-1604908177522-4329e5c52c9b" className="rounded mb-4"/>
          <button
            onClick={async () => {
              await createOrder(token);
              notify("Item added");
            }}
            className="w-full bg-indigo-300 py-2 rounded"
          >
            Add Biryani â‚¹250
          </button>
        </div>

        {/* Cart */}
        <div className="bg-white p-6 rounded-xl col-span-2">
          <h2 className="font-bold mb-4">Orders</h2>

          {orders.map(o => (
            <div key={o.id} className="flex justify-between mb-2">
              <span>{o.item}</span>
              <button
                onClick={async () => {
                  await cancelOrder(o.id, token);
                  notify("Item removed");
                }}
                className="text-red-400"
              >
                Cancel
              </button>
            </div>
          ))}

          <button
            onClick={async () => {
              await checkout(token);
              notify("Checkout done");
            }}
            className="mt-4 bg-green-300 px-4 py-2 rounded"
          >
            Checkout
          </button>
        </div>
      </div>

      {/* Chart */}
      <div className="mt-10 bg-white p-6 rounded-xl">
        <h3 className="font-bold mb-4">Orders Chart</h3>
        <PieChart width={300} height={200}>
          <Pie data={[{name:"Orders", value:orders.length}]} dataKey="value">
            <Cell fill="#7C9AFF"/>
          </Pie>
        </PieChart>
      </div>
    </div>
  );
}
