import { useState, useEffect } from "react";
import Login from "./login";
import { getRestaurants, createOrder, checkoutOrder, cancelOrder, updatePayment } from "./api";

function App() {
  const [user, setUser] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [orders, setOrders] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("");

  // Fetch restaurants when user logs in
  useEffect(() => {
    if (user) {
      async function fetchData() {
        const data = await getRestaurants(user);
        setRestaurants(data);
      }
      fetchData();
    }
  }, [user]);

  // Handle creating an order
  const handleOrder = async (restaurant_id, item) => {
    const result = await createOrder(user, restaurant_id, item);
    alert(result.message);
    setOrders([...orders, { restaurant_id, item, status: "CREATED" }]);
  };

  // Handle checkout
  const handleCheckout = async () => {
    if (orders.length === 0) {
      alert("No orders to checkout!");
      return;
    }
    const result = await checkoutOrder(user);
    alert(result.message);
    setOrders([]); // clear orders after checkout
  };

  // Handle cancel
  const handleCancel = async () => {
    if (orders.length === 0) {
      alert("No orders to cancel!");
      return;
    }
    const result = await cancelOrder(user);
    alert(result.message);
    setOrders([]); // clear orders after cancel
  };

  // Handle update payment
  const handlePayment = async () => {
    if (!paymentMethod) {
      alert("Please select a payment method.");
      return;
    }
    const result = await updatePayment(user, paymentMethod);
    alert(result.message);
  };

  // If no user, show login
  if (!user) return <Login setUser={setUser} />;

  // Dashboard view
  return (
    <div style={{ padding: "20px" }}>
      <h1>Welcome, {user}</h1>

      <h2>Restaurants</h2>
      <ul>
        {restaurants.map((r) => (
          <li key={r.id} style={{ marginBottom: "10px" }}>
            {r.name}{" "}
            <button onClick={() => handleOrder(r.id, "Biryani")}>Order Biryani</button>{" "}
            <button onClick={() => handleOrder(r.id, "Pizza")}>Order Pizza</button>
          </li>
        ))}
      </ul>

      <h2>Orders</h2>
      {orders.length === 0 ? (
        <p>No orders yet</p>
      ) : (
        <ul>
          {orders.map((o, index) => (
            <li key={index}>
              Restaurant ID: {o.restaurant_id}, Item: {o.item}, Status: {o.status}
            </li>
          ))}
        </ul>
      )}

      <h2>Actions</h2>
      <button onClick={handleCheckout} style={{ marginRight: "10px" }}>
        Checkout
      </button>
      <button onClick={handleCancel}>Cancel All Orders</button>

      <h2>Payment Method</h2>
      <select
        value={paymentMethod}
        onChange={(e) => setPaymentMethod(e.target.value)}
      >
        <option value="">--Select Payment Method--</option>
        <option value="Credit Card">Credit Card</option>
        <option value="Debit Card">Debit Card</option>
        <option value="UPI">UPI</option>
        <option value="Cash">Cash</option>
        <option value="Net Banking">Net Banking</option>
      </select>
      <button
        onClick={handlePayment}
        disabled={!paymentMethod}
        style={{ marginLeft: "10px" }}
      >
        Update Payment
      </button>

      <h2>Logout</h2>
      <button onClick={() => setUser(null)}>Logout</button>
    </div>
  );
}

export default App;
