import { useState, useEffect } from "react";
import {
  login,
  getMenu,
  addOrder,
  cancelOrder,
  checkout,
  adminOrders
} from "./api";

import Login from "./components/Login";
import Navbar from "./components/Navbar";
import RestaurantList from "./components/RestaurantList";
import Cart from "./components/Cart";
import AdminDashboard from "./components/AdminDashboard";

export default function App() {
  const [token, setToken] = useState("");
  const [role, setRole] = useState("");
  const [menu, setMenu] = useState({});
  const [orders, setOrders] = useState([]);
  const [payment, setPayment] = useState("CREDIT_CARD");

  const handleLogin = async (u) => {
    const r = await login(u);
    setToken(r.token);
    setRole(r.role);
    setMenu(await getMenu(r.token));
  };

  useEffect(() => {
    if (role === "ADMIN") {
      const i = setInterval(() => {
        adminOrders(token).then(setOrders);
      }, 1000);
      return () => clearInterval(i);
    }
  }, [role, token]);

  if (!token) return <Login onLogin={handleLogin} />;

  return (
    <>
      <Navbar role={role} logout={() => window.location.reload()} />

      <div className="p-6">
        {role === "ADMIN" && <AdminDashboard orders={orders} />}

        <div className="grid grid-cols-3 gap-6">
          <div className="col-span-2">
            <RestaurantList
              menu={menu}
              onAdd={(i, p) => addOrder(token, i, p)}
            />
          </div>

          <div>
            <Cart
              orders={orders.filter(o => o.status === "CREATED")}
              onCancel={(id) => cancelOrder(id, token)}
            />

            {role !== "MEMBER" && (
              <div className="bg-white p-4 rounded-xl shadow mt-4 fade-in">
                <select
                  className="border p-2 w-full rounded mb-2"
                  onChange={(e) => setPayment(e.target.value)}
                >
                  <option>CREDIT_CARD</option>
                  <option>UPI</option>
                  <option>CASH</option>
                </select>

                <button
                  onClick={() => checkout(token, payment)}
                  className="w-full bg-green-500 hover:bg-green-600 text-white py-2 rounded"
                >
                  Checkout
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}
