import { useEffect, useState } from "react";

const images = [
  "https://images.unsplash.com/photo-1504674900247-0877df9cc836",
  "https://images.unsplash.com/photo-1540189549336-e6e99c3679fe",
  "https://images.unsplash.com/photo-1600891964599-f61ba0e24092"
];

export default function Login({ onLogin }) {
  const [i, setI] = useState(0);

  useEffect(() => {
    const t = setInterval(() => setI((i+1)%images.length), 3000);
    return () => clearInterval(t);
  }, [i]);

  return (
    <div className="h-screen relative">
      <img src={images[i]} className="absolute inset-0 w-full h-full object-cover transition-all duration-1000"/>
      <div className="absolute inset-0 bg-black/40 flex justify-center items-center">
        <div className="bg-white p-10 rounded-xl shadow w-96 text-center">
          <h1 className="text-2xl font-bold text-orange-600 mb-6">FoodDash</h1>
          {["ADMIN","MANAGER","MEMBER"].map(r=>(
            <button key={r}
              onClick={()=>onLogin(r)}
              className="w-full mb-3 bg-orange-500 text-white py-2 rounded hover:bg-orange-600 transition">
              Login as {r}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
