const BASE = "http://localhost:8000";

export const getRestaurants = (user) =>
  fetch(`${BASE}/restaurants?username=${user}`).then(r => r.json());

export const createOrder = (user, rid, item) =>
  fetch(`${BASE}/order?username=${user}&restaurant_id=${rid}&item=${item}`, { method: "POST" });

export const checkoutOrder = (user) =>
  fetch(`${BASE}/checkout?username=${user}`, { method: "POST" });

export const cancelOrder = (user) =>
  fetch(`${BASE}/cancel?username=${user}`, { method: "POST" });

export const updatePayment = (user, method) =>
  fetch(`${BASE}/payment?username=${user}&method=${method}`, { method: "POST" });

export const getAdminOrders = (user) =>
  fetch(`${BASE}/admin/orders?username=${user}`).then(r => r.json());
