      <h3 className="font-bold mb-2">üìç {country}</h3>
      {items.map(i => (
        <button key={i.item}
          onClick={() => onAdd(i.item, i.price)}
          className="block w-full mb-2 bg-indigo-100 p-2 rounded">
          Add {i.item} ‚Ä¢ ‚Çπ{i.price}
        </button>
      ))}
    </div>
  ));
}
