import { useState, useEffect } from "react";
import { login, getMenu, addOrder, checkout, adminOrders } from "./api";
import Login from "./components/login";
import Navbar from "./components/Navbar";
import RestaurantList from "./components/RestaurantList";
import AdminDashboard from "./components/AdminDashboard";

export default function App() {
  const [token, setToken] = useState("");
  const [role, setRole] = useState("");
  const [menu, setMenu] = useState({});
  const [orders, setOrders] = useState([]);
  const [payment, setPayment] = useState("CREDIT_CARD");

  const handleLogin = async (u) => {
    const r = await login(u);
    setToken(r.token);
    setRole(r.role);
    setMenu(await getMenu(r.token));
  };

  useEffect(() => {
    if (role === "ADMIN") {
      const i = setInterval(() => {
        adminOrders(token).then(setOrders);
      }, 1000);
      return () => clearInterval(i);
    }
  }, [role, token]);

  if (!token) return <Login onLogin={handleLogin} />;

  return (
    <>
      <Navbar role={role} logout={() => window.location.reload()} />
      <div className="p-6 grid grid-cols-2 gap-6">
        <RestaurantList menu={menu} onAdd={(i,p)=>addOrder(token,i,p)} />

        {role !== "MEMBER" && (
          <div className="bg-white p-4 rounded shadow">
            <select onChange={e=>setPayment(e.target.value)}
              className="border p-2 mb-2 w-full">
              <option>CREDIT_CARD</option>
              <option>UPI</option>
              <option>CASH</option>
            </select>
            <button onClick={()=>checkout(token,payment)}
              className="w-full bg-green-400 py-2 rounded">
              Checkout
            </button>
          </div>
        )}
      </div>

      {role === "ADMIN" && <AdminDashboard orders={orders} />}
    </>
  );
}
