const icons = {
  Biryani: "üçñ",
  Burger: "ü•ó",
  Drinks: "ü•§"
};

export default function RestaurantList({ menu, onAdd }) {
  return Object.entries(menu).map(([country, items]) => (
    <div key={country} className="bg-white p-4 rounded-xl shadow mb-6">
      <h3 className="font-bold mb-3 text-[#87A96B]">üìç {country}</h3>
      {items.map(i => (
        <div
          key={i.item}
          className="flex items-center gap-4 border rounded-lg p-2 mb-2 hover:shadow-lg transition transform hover:scale-105"
        >
          <div className="text-2xl">{icons[i.item] || "üç¥"}</div>
          <div className="flex-1">
            <p className="font-semibold">{i.item}</p>
            <p className="text-[#E2725B] font-bold">‚Çπ{i.price}</p>
          </div>
          <button
            onClick={() => onAdd(i.item, i.price)}
            className="bg-[#E2725B] hover:bg-[#c96150] text-white px-4 py-1 rounded transition transform hover:scale-110"
          >
            Add
          </button>
        </div>
      ))}
    </div>
  ));
}
