const images = {
  Biryani: "https://images.unsplash.com/photo-1604908177522-4329e5c52c9b",
  Burger: "C:\Users\Admin\Desktop\food-ordering-app\biryani.jfif",
};

export default function RestaurantList({ menu, onAdd }) {
  return Object.entries(menu).map(([country, items]) => (
    <div key={country} className="bg-white p-4 rounded-xl shadow mb-6">
      <h3 className="font-bold text-lg mb-3">üìç {country}</h3>
      {items.map(i => (
        <div key={i.item} className="flex gap-4 items-center border rounded p-2 mb-2">
          <img src={images[i.item]} className="w-16 h-16 rounded object-cover" />
          <div className="flex-1">
            <p className="font-medium">{i.item}</p>
            <p className="text-sm text-gray-500">‚Çπ{i.price}</p>
          </div>
          <button
            onClick={() => onAdd(i.item, i.price)}
            className="bg-orange-500 text-white px-3 py-1 rounded hover:bg-orange-600"
          >
            Add
          </button>
        </div>
      ))}
    </div>
  ));
}
