      {orders.map(o => (
        <div key={o.id} className="flex justify-between py-2 text-sm border-b">
          <span>{o.item}</span>
          <span className="text-[color:var(--terracotta)] font-medium">
            â‚¹{o.total}
          </span>
          <button
            onClick={() => onCancel(o.id)}
